
public class Triangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<3;i++) {
			System.out.println(i);
			if(i==0) {
				System.out.println("*");
				}
			for(int j=0;j<i+2;j++) {
				System.out.print("*");
				System.out.print("\t");
			}
			System.out.println();
		}
			

	}

}
